import React, { useState } from "react";

export default function Header() {
  const [theme, setTheme] = useState("light");

  const changeTheme = (value) => {
    setTheme(value);
    if (value === "dark") {
      document.documentElement.classList.add("dark");
    } else {
      document.documentElement.classList.remove("dark");
    }
  };

  return (
    <header className="flex items-center justify-between px-6 py-3 bg-white dark:bg-gray-900 shadow">
      {/* Logo */}
      <div className="flex items-center gap-2">
        <img src="/logo.png" alt="Logo" className="h-10" />
        <span className="font-bold text-lg text-gray-800 dark:text-gray-100">
          SIJILAT
        </span>
      </div>

      {/* Menu */}
      <nav className="flex gap-6">
        <button className="text-gray-700 dark:text-gray-200">Home</button>
        <button className="text-gray-700 dark:text-gray-200">Contact Us</button>
      </nav>

      {/* Theme Selector */}
      <select
        value={theme}
        onChange={(e) => changeTheme(e.target.value)}
        className="border rounded px-2 py-1 text-sm dark:bg-gray-800 dark:text-gray-100"
      >
        <option value="light">Light</option>
        <option value="dark">Dark</option>
      </select>
    </header>
  );
}
