import React, { createContext, useState, useEffect, useContext } from 'react';

// Tailwind theme classes
const themeClasses = {
  light: 'bg-white text-black',
  dark: 'bg-gray-900 text-white',
  blue: 'bg-blue-900 text-white',
  green: 'bg-green-700 text-white',
};

const defaultTheme = 'light';

// 1. Create Context
const ThemeContext = createContext();

// 2. Context Provider
export const ThemeProvider = ({ children }) => {
  const [theme, setTheme] = useState(() => {
    return localStorage.getItem('theme') || defaultTheme;
  });

  useEffect(() => {
    // Remove all previous theme classes from body
    document.body.className = '';
    // ✅ split by space, not by character
    document.body.classList.add(...themeClasses[theme].split(' '));
    localStorage.setItem('theme', theme);
  }, [theme]);

  return (
    <ThemeContext.Provider value={{ theme, setTheme, themeClasses }}>
      {children}
    </ThemeContext.Provider>
  );
};

// 3. Custom Hook
export const useTheme = () => useContext(ThemeContext);
