import React, { useState, useEffect } from 'react';

// Enum for theme names
const themeEnum = {
  LIGHT: 'light',
  DARK: 'dark',
  BLUE: 'blue',
  GREEN: 'green',
};

const Header = () => {
  const [theme, setTheme] = useState('light'); // Default theme is light
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false); // State to toggle mobile menu

  // Update the theme on body element
  useEffect(() => {
    document.body.classList.remove('light', 'dark', 'blue', 'green');
    document.body.classList.add(theme); // Add the appropriate theme class
  }, [theme]);

  // Function to handle theme change
  const handleThemeChange = (event) => {
    setTheme(event.target.value); // Update state with the new theme
  };

  // Toggle mobile menu visibility
  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen); // Toggle the menu
  };

  return (
    <header
      className="p-4 shadow-md"
      style={{
        backgroundColor: "var(--header-bg)", // Using the theme-specific header background color
      }}
    >
      <div className="max-w-7xl mx-auto flex items-center justify-between px-6">
        {/* Logo and Company Name - Left side */}
        <div className="flex items-center space-x-4">
          <img src="/images/logo.png" alt="Logo" className="h-10" />
          <span className="text-2xl font-semibold text-primary">Your Company</span>
        </div>

        {/* Navigation Links (Home, Contact) - Centered */}
        <div className="flex-grow  justify-center space-x-6">
          <a href="#" className="text-gray-700 hover:text-primary transition">Home</a>
          <a href="#" className="text-gray-700 hover:text-primary transition">Contact</a>
        </div>

        {/* Theme Selector - Right side */}
        <div className="flex items-center">
          <select
            onChange={handleThemeChange}
            value={theme}
            className="bg-gray-200 text-gray-700 py-2 px-4 rounded-md shadow-md focus:outline-none"
          >
            {Object.keys(themeEnum).map((key) => (
              <option key={key} value={themeEnum[key]}>
                {themeEnum[key].charAt(0).toUpperCase() + themeEnum[key].slice(1)} Theme
              </option>
            ))}
          </select>
        </div>

       
      </div>

     
    </header>
  );
};

export default Header;
