import React, { useState, useEffect } from 'react';

// Enum for theme names
const themeEnum = {
  LIGHT: 'light',
  DARK: 'dark',
  BLUE: 'blue',
  GREEN: 'green',
};

const Header = () => {
  const [theme, setTheme] = useState('light'); // Default theme is light

  // Update the theme on body element
  useEffect(() => {
    document.body.classList.remove('light', 'dark', 'blue', 'green');
    document.body.classList.add(theme); // Add the theme class
  }, [theme]);

  // Function to handle theme change
  const handleThemeChange = (event) => {
    setTheme(event.target.value); // Update state with the new theme
  };

  return (
    <header className="bg-primary p-4 shadow-md">
      <div className="max-w-7xl mx-auto flex items-center justify-between">
        {/* Logo and Company Name */}
        <div className="flex items-center space-x-4">
          <img src="/images/logo.png" alt="Logo" className="h-10" />
          <span className="text-2xl font-semibold text-white">Your Company</span>
        </div>

        {/* Navigation Links */}
        <nav className="hidden md:flex space-x-6">
          <a href="#" className="text-white hover:text-gray-200">Home</a>
          <a href="#" className="text-white hover:text-gray-200">About</a>
          <a href="#" className="text-white hover:text-gray-200">Services</a>
          <a href="#" className="text-white hover:text-gray-200">Contact</a>
        </nav>

        {/* Theme Selector Dropdown */}
        <div className="relative">
          <select
            onChange={handleThemeChange}
            value={theme}
            className="bg-primary text-white p-2 rounded-md shadow-lg focus:outline-none"
          >
            {Object.keys(themeEnum).map((key) => (
              <option key={key} value={themeEnum[key]}>
                {themeEnum[key].charAt(0).toUpperCase() + themeEnum[key].slice(1)} Theme
              </option>
            ))}
          </select>
        </div>
      </div>
    </header>
  );
};

export default Header;
