import React, { useState, useEffect } from 'react';

// Enum for theme names
const themeEnum = {
  LIGHT: 'light',
  DARK: 'dark',
  BLUE: 'blue',
  GREEN: 'green',
};

const Header = () => {
  const [theme, setTheme] = useState('light'); // Default theme is light
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false); // State to toggle mobile menu

  // Update the theme on the body element
  useEffect(() => {
    document.body.classList.remove('light', 'dark', 'blue', 'green');
    document.body.classList.add(theme); // Add the appropriate theme class
  }, [theme]);

  // Function to handle theme change
  const handleThemeChange = (event) => {
    setTheme(event.target.value); // Update state with the new theme
  };

  // Toggle mobile menu visibility
  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  return (
    <header className="bg-primary p-4 shadow-md">
      <div className="max-w-7xl mx-auto flex items-center justify-between">
        {/* Logo and Company Name - Left side */}
        <div className="flex items-center space-x-4">
          <img src="/images/logo.png" alt="Logo" className="h-10" />
          <span className="text-2xl font-semibold text-white">Your Company</span>
        </div>

        {/* Navigation Links and Theme Selector - Right side */}
        <div className="flex items-center space-x-6">
          {/* Navigation Links */}
          <nav className="hidden md:flex space-x-6">
            <a href="#" className="text-white hover:text-gray-200">Home</a>
            <a href="#" className="text-white hover:text-gray-200">Contact</a>
          </nav>

          {/* Theme Selector Dropdown */}
          <div className="relative">
            <select
              onChange={handleThemeChange}
              value={theme}
              className="bg-primary text-white p-2 rounded-md shadow-lg focus:outline-none"
            >
              {Object.keys(themeEnum).map((key) => (
                <option key={key} value={themeEnum[key]}>
                  {themeEnum[key].charAt(0).toUpperCase() + themeEnum[key].slice(1)} Theme
                </option>
              ))}
            </select>
          </div>

          {/* Hamburger Menu for Mobile */}
          <div className="md:hidden">
            <button
              className="text-white p-2"
              onClick={toggleMobileMenu}
            >
              <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor" className="h-6 w-6">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 6h16M4 12h16M4 18h16" />
              </svg>
            </button>
          </div>
        </div>
      </div>

      {/* Mobile Menu - Dropdown */}
      <div className={`${isMobileMenuOpen ? 'block' : 'hidden'} md:hidden bg-primary text-white p-4 space-y-4`}>
        <a href="#" className="hover:text-gray-200">Home</a>
        <a href="#" className="hover:text-gray-200">Contact</a>
        {/* Theme Selector in Mobile */}
        <select
          onChange={handleThemeChange}
          value={theme}
          className="bg-primary text-white p-2 rounded-md shadow-lg focus:outline-none"
        >
          {Object.keys(themeEnum).map((key) => (
            <option key={key} value={themeEnum[key]}>
              {themeEnum[key].charAt(0).toUpperCase() + themeEnum[key].slice(1)} Theme
            </option>
          ))}
        </select>
      </div>
    </header>
  );
};

export default Header;
