import React, { useState, useEffect } from 'react';

// Enum for theme names
const themeEnum = {
  LIGHT: 'light',
  DARK: 'dark',
  BLUE: 'blue',
  GREEN: 'green',
};

const Header = () => {
  const [theme, setTheme] = useState('light');
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  // Update the theme on <body>
  useEffect(() => {
    document.body.classList.remove(...Object.values(themeEnum));
    document.body.classList.add(theme);
  }, [theme]);

  const handleThemeChange = (event) => {
    setTheme(event.target.value);
  };

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  return (
    <header className="fixed top-0 left-0 right-0 z-50 bg-white border-b-4 border-[#0b2a4a] shadow-md">
      {/* Top Bar */}
      <div className="max-w-screen-xl mx-auto px-6 py-3 flex justify-between items-center">
        {/* Logo + Text */}
        <div className="flex items-center space-x-3">
          <img src="/images/logo.png" alt="Logo" className="h-16 w-auto" />
          <div className="text-xs leading-tight text-gray-800">
            <div className="font-bold text-right">سجلات</div>
            <div className="font-semibold">SIJILAT</div>
            <div className="text-right">نظام السجلات التجارية</div>
            <div>COMMERCIAL REGISTRATION PORTAL</div>
          </div>
        </div>

        {/* Desktop Nav Center */}
        <nav className="hidden md:flex items-center gap-8 text-sm font-semibold text-[#0b2a4a]">
          <a href="#" className="hover:underline">PLAN <span className="text-gray-400">▾</span></a>
          <a href="#" className="hover:underline">START <span className="text-gray-400">▾</span></a>
          <a href="#" className="hover:underline">MANAGE <span className="text-gray-400">▾</span></a>
          <a href="#" className="hover:underline">BUSINESS SEARCH <span className="text-gray-400">▾</span></a>
          <a href="#" className="hover:underline">E-SERVICES</a>
          <a href="#" className="hover:underline">REGULATIONS <span className="text-gray-400">▾</span></a>
        </nav>

        {/* Right Buttons & Select */}
        <div className="flex items-center space-x-3 text-sm">
          <a href="#" className="text-gray-700 hover:underline hidden md:inline">About Sijilat</a>
          <a href="#" className="text-gray-700 hover:underline hidden md:inline">Contact</a>
          <a href="#" className="text-gray-700 hover:underline hidden md:inline">عربي</a>
          <span className="text-xl hidden md:inline">🌐</span>

          <button className="bg-[#0b2a4a] text-white px-3 py-1 text-xs rounded hidden md:inline">🔒 LOGIN</button>
          <button className="bg-[#0b2a4a] text-white px-3 py-1 text-xs rounded hidden md:inline">🔒 E-KEY 2.0 LOGIN</button>
          <button className="bg-[#0b2a4a] text-white px-3 py-1 text-xs rounded hidden md:inline">🔒 GCC LOGIN</button>

          {/* Theme Selector */}
          <select
            onChange={handleThemeChange}
            value={theme}
            className="bg-gray-100 text-gray-700 py-1 px-2 rounded-md shadow-sm text-xs focus:outline-none"
          >
            {Object.keys(themeEnum).map((key) => (
              <option key={key} value={themeEnum[key]}>
                {themeEnum[key].charAt(0).toUpperCase() + themeEnum[key].slice(1)} Theme
              </option>
            ))}
          </select>

          {/* Mobile Hamburger */}
          <button className="md:hidden p-2 text-gray-700" onClick={toggleMobileMenu}>
            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor" className="h-6 w-6">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 6h16M4 12h16M4 18h16" />
            </svg>
          </button>
        </div>
      </div>

      {/* Mobile Menu */}
      <div className={`${isMobileMenuOpen ? 'block' : 'hidden'} md:hidden bg-white text-gray-800 px-6 py-4 space-y-2`}>
        <a href="#" className="block py-1">PLAN ▾</a>
        <a href="#" className="block py-1">START ▾</a>
        <a href="#" className="block py-1">MANAGE ▾</a>
        <a href="#" className="block py-1">BUSINESS SEARCH ▾</a>
        <a href="#" className="block py-1">E-SERVICES</a>
        <a href="#" className="block py-1">REGULATIONS ▾</a>
        <hr />
        <a href="#" className="block py-1">About Sijilat</a>
        <a href="#" className="block py-1">Contact</a>
        <a href="#" className="block py-1">عربي</a>
        <div className="flex flex-col gap-2 mt-2">
          <button className="bg-[#0b2a4a] text-white px-3 py-1 text-xs rounded">🔒 LOGIN</button>
          <button className="bg-[#0b2a4a] text-white px-3 py-1 text-xs rounded">🔒 E-KEY 2.0 LOGIN</button>
          <button className="bg-[#0b2a4a] text-white px-3 py-1 text-xs rounded">🔒 GCC LOGIN</button>
        </div>
      </div>
    </header>
  );
};

export default Header;
