import React, { useState } from 'react';

const LoginForm = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission (e.g., make an API request)
    console.log("Logging in with:", email, password);
  };

  return (
    <div
      className="max-w-md mx-auto p-8 shadow-lg rounded-lg"
      style={{
        backgroundColor: "var(--form-bg)", // Using theme-specific form background color
      }}
    >
      <h2 className="text-2xl font-semibold text-center text-primary mb-4">Login</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        {/* Email */}
        <div>
          <label htmlFor="email" className="block text-sm font-medium text-gray-700">Email</label>
          <input
            id="email"
            type="email"
            className="w-full p-3 border border-gray-300 rounded-md"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>

        {/* Password */}
        <div>
          <label htmlFor="password" className="block text-sm font-medium text-gray-700">Password</label>
          <input
            id="password"
            type="password"
            className="w-full p-3 border border-gray-300 rounded-md"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>

        {/* Submit Button */}
        <div>
          <button
            type="submit"
            className="w-full py-3 bg-primary text-white font-semibold rounded-md hover:bg-button-hover"
            style={{
              backgroundColor: "var(--button-bg)", // Using the theme-specific button color
            }}
          >
            Log In
          </button>
        </div>
      </form>

      {/* Register Link */}
      <div className="mt-4 text-center">
        <p className="text-sm">Don't have an account? <a href="/register" className="text-primary hover:underline">Register</a></p>
      </div>
    </div>
  );
};

export default LoginForm;
