import React, { createContext, useState, useEffect, useContext } from "react";

const themes = {
  light: {
    "--form-bg": "#ffffff",
    "--button-bg": "#2563eb", // Tailwind blue-600
    "--button-hover": "#1d4ed8", // Tailwind blue-700
    "--text-primary": "#111827", // gray-900
  },
  dark: {
    "--form-bg": "#1f2937", // gray-800
    "--button-bg": "#4b5563", // gray-600
    "--button-hover": "#374151", // gray-700
    "--text-primary": "#f9fafb", // gray-50
  },
  blue: {
    "--form-bg": "#e0f2fe", // light blue
    "--button-bg": "#0284c7", // sky-600
    "--button-hover": "#0369a1", // sky-700
    "--text-primary": "#0c4a6e", // sky-900
  },
  green: {
    "--form-bg": "#dcfce7", // light green
    "--button-bg": "#16a34a", // green-600
    "--button-hover": "#15803d", // green-700
    "--text-primary": "#14532d", // green-900
  },
};

const defaultTheme = "light";

const ThemeContext = createContext();

export const ThemeProvider = ({ children }) => {
  const [theme, setTheme] = useState(
    () => localStorage.getItem("theme") || defaultTheme
  );

  useEffect(() => {
    localStorage.setItem("theme", theme);

    // apply CSS variables to :root
    const root = document.documentElement;
    const themeVars = themes[theme];
    Object.entries(themeVars).forEach(([key, value]) => {
      root.style.setProperty(key, value);
    });
  }, [theme]);

  return (
    <ThemeContext.Provider value={{ theme, setTheme }}>
      {children}
    </ThemeContext.Provider>
  );
};

export const useTheme = () => useContext(ThemeContext);
