import React, { useState, useEffect } from 'react';

// Enum for theme names
const themeEnum = {
  LIGHT: 'light',
  DARK: 'dark',
  BLUE: 'blue',
  GREEN: 'green',
};

const Header = () => {
  const [theme, setTheme] = useState('light'); // Default theme is light
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false); // Mobile menu state

  // Update the theme on body element
  useEffect(() => {
    document.body.classList.remove('light', 'dark', 'blue', 'green');
    document.body.classList.add(theme); // Add the appropriate theme class
  }, [theme]);

  // Function to handle theme change
  const handleThemeChange = (event) => {
    setTheme(event.target.value); // Update state with the new theme
  };

  // Toggle mobile menu visibility
  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen); // Toggle the menu
  };

  return (
    <header className="fixed top-0 left-0 right-0 z-50 bg-white shadow-md">
      <div className="max-w-screen-xl mx-auto px-6 py-4 flex justify-between items-center">
        {/* Logo on Left */}
        <div className="flex items-center space-x-4">
          <img src="/images/logo.png" alt="Logo" className="h-8" />
        </div>

        {/* Centered Navigation Links */}
        <div className="hidden md:flex items-center space-x-12">
          <a href="#" className="text-gray-800 hover:text-primary font-medium">Home</a>
          <a href="#" className="text-gray-800 hover:text-primary font-medium">About</a>
          <a href="#" className="text-gray-800 hover:text-primary font-medium">Services</a>
          <a href="#" className="text-gray-800 hover:text-primary font-medium">Contact</a>
        </div>

        {/* Right side: Theme dropdown or Search */}
        <div className="flex items-center space-x-4">
          <select
            onChange={handleThemeChange}
            value={theme}
            className="bg-gray-100 text-gray-700 py-2 px-4 rounded-md shadow-sm focus:outline-none"
          >
            {Object.keys(themeEnum).map((key) => (
              <option key={key} value={themeEnum[key]}>
                {themeEnum[key].charAt(0).toUpperCase() + themeEnum[key].slice(1)} Theme
              </option>
            ))}
          </select>

          {/* Hamburger for mobile menu */}
          <button className="md:hidden p-2 text-gray-700" onClick={toggleMobileMenu}>
            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor" className="h-6 w-6">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 6h16M4 12h16M4 18h16" />
            </svg>
          </button>
        </div>
      </div>

      {/* Mobile Menu (Visible when hamburger is clicked) */}
      <div className={`${isMobileMenuOpen ? 'block' : 'hidden'} md:hidden bg-white text-gray-800 p-4`}>
        <a href="#" className="block py-2">Home</a>
        <a href="#" className="block py-2">About</a>
        <a href="#" className="block py-2">Services</a>
        <a href="#" className="block py-2">Contact</a>
      </div>
    </header>
  );
};

export default Header;
