import React, { useState, useEffect } from 'react';

// Enum for theme names
const themeEnum = {
  LIGHT: 'light',
  DARK: 'dark',
  BLUE: 'blue',
  GREEN: 'green',
};

const Header = () => {
  const [theme, setTheme] = useState('light'); // Default theme is light

  // Update the theme on body element
  useEffect(() => {
    document.body.classList.remove('light', 'dark', 'blue', 'green');
    document.body.classList.add(theme); // Add the theme class to the body
  }, [theme]);

  // Function to handle theme change
  const handleThemeChange = (newTheme) => {
    setTheme(newTheme); // Update state with the new theme
  };

  return (
    <header className="p-4" style={{ backgroundColor: "var(--primary)", boxShadow: `0 4px 6px var(--shadow)` }}>
      <div className="flex items-center justify-between max-w-7xl mx-auto">
        {/* Logo and Company Name */}
        <div className="flex items-center space-x-2">
          <img src="/images/logo.png" alt="Logo" className="h-10" />
          <span className="text-2xl font-semibold" style={{ color: "var(--secondary)" }}>Your Company</span>
        </div>

        {/* Navigation Links */}
        <nav className="space-x-4 flex items-center">
          <a href="#" style={{ color: "var(--secondary)" }} className="hover:text-gray-200">Home</a>
          <a href="#" style={{ color: "var(--secondary)" }} className="hover:text-gray-200">About</a>
          <a href="#" style={{ color: "var(--secondary)" }} className="hover:text-gray-200">Services</a>
          <a href="#" style={{ color: "var(--secondary)" }} className="hover:text-gray-200">Contact</a>

          {/* Theme Selector Dropdown */}
          <div className="relative">
            <button className="text-white bg-primary px-4 py-2 rounded-md shadow-lg">
              Theme
            </button>
            <div className="absolute right-0 mt-2 w-48 bg-white rounded-lg shadow-lg border border-gray-200">
              <ul>
                {Object.keys(themeEnum).map((key) => (
                  <li key={key}>
                    <button
                      className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-200"
                      onClick={() => handleThemeChange(themeEnum[key])}
                    >
                      {themeEnum[key].charAt(0).toUpperCase() + themeEnum[key].slice(1)} Theme
                    </button>
                  </li>
                ))}
              </ul>
            </div>
          </div>
        </nav>
      </div>
    </header>
  );
};

export default Header;
