import React, { createContext, useState, useEffect, useContext } from "react";

// Separate theme classes for header, body, footer
const themeClasses = {
  light: {
    header: "bg-gray-200 text-black",
    body: "bg-white text-black",
    footer: "bg-gray-300 text-black",
  },
  dark: {
    header: "bg-gray-800 text-white",
    body: "bg-gray-900 text-white",
    footer: "bg-gray-700 text-white",
  },
  blue: {
    header: "bg-blue-800 text-white",
    body: "bg-blue-900 text-white",
    footer: "bg-blue-700 text-white",
  },
  green: {
    header: "bg-green-800 text-white",
    body: "bg-green-900 text-white",
    footer: "bg-green-700 text-white",
  },
};

const defaultTheme = "light";

// Create Context
const ThemeContext = createContext();

export const ThemeProvider = ({ children }) => {
  const [theme, setTheme] = useState(
    () => localStorage.getItem("theme") || defaultTheme
  );

  useEffect(() => {
    localStorage.setItem("theme", theme);
  }, [theme]);

  return (
    <ThemeContext.Provider value={{ theme, setTheme, themeClasses }}>
      {children}
    </ThemeContext.Provider>
  );
};

export const useTheme = () => useContext(ThemeContext);
