import React, { useState } from 'react';
import { useTheme } from '../context/ThemeContext'; // Make sure the path is correct

const LoginForm = () => {
  const { currentThemeClasses = {} } = useTheme(); // Get styles from context
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Logging in with:", email, password);
  };

  return (
    <div className={`max-w-md mx-auto p-8 shadow-lg rounded-lg ${currentThemeClasses.form}`}>
      <h2 className="text-2xl font-semibold text-center mb-4">Login</h2>

      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label htmlFor="email" className="block text-sm font-medium mb-1">Email</label>
          <input
            id="email"
            type="email"
            className="w-full p-3 border border-gray-300 rounded-md"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>

        <div>
          <label htmlFor="password" className="block text-sm font-medium mb-1">Password</label>
          <input
            id="password"
            type="password"
            className="w-full p-3 border border-gray-300 rounded-md"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>

        <div>
          <button
            type="submit"
            className={`w-full py-3 font-semibold rounded-md ${currentThemeClasses.button}`}
          >
            Log In
          </button>
        </div>
      </form>

      <div className="mt-4 text-center">
        <p className="text-sm">
          Don't have an account?{' '}
          <a href="/register" className="underline font-medium">
            Register
          </a>
        </p>
      </div>
    </div>
  );
};

export default LoginForm;
