// src/App.js
import React, { useState, useEffect } from 'react';
import Header from './components/Header';
import Footer from './components/Footer';

function App() {
  const [theme, setTheme] = useState('light'); // Default theme is light

  // Set the theme on body class
  useEffect(() => {
    document.body.classList.remove('light', 'dark', 'blue', 'green');
    document.body.classList.add(theme); // Add the appropriate theme class
  }, [theme]);

  const handleThemeChange = (newTheme) => {
    setTheme(newTheme); // Set new theme
  };

  return (
    <div className="min-h-screen">
      <Header />
      <main className="flex-grow p-8" style={{ backgroundColor: "var(--background)", color: "var(--text)" }}>
        <h1 className="text-4xl font-bold text-center" style={{ color: "var(--primary)" }}>
          Welcome to Your Website
        </h1>
        <p className="text-center text-lg mt-4" style={{ color: "var(--secondary)" }}>
          This is the {theme.charAt(0).toUpperCase() + theme.slice(1)} Mode.
        </p>

        <div className="flex justify-center mt-8">
          <button
            onClick={() => handleThemeChange('light')}
            className="px-6 py-3 mx-2 rounded-full bg-primary text-white hover:bg-secondary"
            style={{ boxShadow: `0 4px 6px var(--shadow)` }}
          >
            Light Theme
          </button>
          <button
            onClick={() => handleThemeChange('dark')}
            className="px-6 py-3 mx-2 rounded-full bg-primary text-white hover:bg-secondary"
            style={{ boxShadow: `0 4px 6px var(--shadow)` }}
          >
            Dark Theme
          </button>
          <button
            onClick={() => handleThemeChange('blue')}
            className="px-6 py-3 mx-2 rounded-full bg-primary text-white hover:bg-secondary"
            style={{ boxShadow: `0 4px 6px var(--shadow)` }}
          >
            Blue Theme
          </button>
          <button
            onClick={() => handleThemeChange('green')}
            className="px-6 py-3 mx-2 rounded-full bg-primary text-white hover:bg-secondary"
            style={{ boxShadow: `0 4px 6px var(--shadow)` }}
          >
            Green Theme
          </button>
        </div>
      </main>
      <Footer />
    </div>
  );
}

export default App;
