import React from 'react';
import { useTheme } from '../context/ThemeContext';

const Header = () => {
  const { theme, setTheme } = useTheme();

  return (
    <header className="border-b-4 border-primary shadow-md">
      <div className="max-w-screen-xl mx-auto px-4 py-3 flex justify-between items-center">
        {/* Logo */}
        <div className="flex items-center gap-3">
          <img src="/images/logo.png" alt="Logo" className="h-14" />
          <div className="text-xs leading-tight">
            <div className="text-right font-bold">سجلات</div>
            <div className="font-semibold">SIJILAT</div>
            <div className="text-right">نظام السجلات التجارية</div>
            <div>COMMERCIAL REGISTRATION PORTAL</div>
          </div>
        </div>

        {/* Theme Selector */}
        <select
          value={theme}
          onChange={(e) => setTheme(e.target.value)}
          className="text-sm border px-2 py-1 rounded bg-gray-100"
        >
          <option value="light">Light</option>
          <option value="dark">Dark</option>
          <option value="blue">Blue</option>
          <option value="green">Green</option>
        </select>
      </div>
    </header>
  );
};

export default Header;
